import {useState, useEffect} from 'react'

function Admin(){
    const [link, setLink] = useState('');
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [list, setList] = useState([]);


    const Send_data_to_serv = async(e) => {
        //e.preventDefault();
        try{
            const form_data = new FormData();
            form_data.append('title', title);
            form_data.append('text', text);
            form_data.append('link', link);

            const response = await fetch(
                "http://localhost:5001/send_from_api",{
                    method: "POST",
                    body: form_data
                }
            )

            const data = await response.json()
            console.log(data.res)
        }
        catch(e){
            console.log("Error: ", e);
        }
    }

    const deleteItem = async(id) => {
        try{
            const del_item = new FormData()
            del_item.append('del_id', id)

            const response = await fetch(
                "http://localhost:5001/delete_item", {
                    method: "POST",
                    body: del_item
                }
            )

            const data = await response.json()
            console.log(data.res)

            await setList(list.filter(item => item[0] !== id))
        }
        catch(err){
            console.log("Error", err)
        }
    }

    useEffect(() => {
        fetch("http://localhost:5001/api")
        .then(response => response.json())
        .then(list => setList(list))
        .catch((e) => {console.log("Error - не могу считать", e)})
    }, [])




    return(
      <>
        <form onSubmit={Send_data_to_serv}>
            <input type='text' placeholder='Title' onChange={(e) => setTitle(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Text' onChange={(e) => setText(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Link' onChange={(e) => setLink(e.target.value)}/><br/><br/>
            <button type='submit'>Send</button>
        </form>
        <div className='list_main'>
            {
                list.map(item => (
                    <div className='list_item'>
                        <h3>{item[1]}</h3>
                        <p>{item[2]}</p>
                        <img src={item[3]}></img>
                        <form onChange={() => {deleteItem(item[0])}}>
                            <button type='submit'>Delete</button>
                        </form>
                    </div>
                ))
            }
        </div>
      </>
    );
}

export default Admin;
