import {useState, useEffect} from 'react'
import './style/AdminPage.css'

function Admin(){
    const [link, setLink] = useState('');
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [list, setList] = useState([]);

    const [data_link, setDataLink] = useState([]);


    const Send_data_to_serv = async() => {
        try{
            const form_data = new FormData();
            form_data.append('title', title);
            form_data.append('text', text);
            form_data.append('link', JSON.stringify(data_link));

            const response = await fetch(
                "http://localhost:5001/send_from_api",{
                    method: "POST",
                    body: form_data
                }
            )

            const data = await response.json()
            console.log(data.res)
        }
        catch(e){
            console.log("Error: ", e);
        }
    }

    const deleteItem = async (id) => {
        try{
            const form_data = new FormData()
            form_data.append('del', id)
            const response = await fetch(
                "http://localhost:5001/delete_item", {
                    method: "POST",
                    body: form_data
                }
            )
            const data = await response.json()
            console.log(data.res)

            setList(list.filter(item => item[0] !== id))
        }
        catch(err){
            console.log(err)
        }
    }

    const plusLink = (e) => {
        e.preventDefault();
        setDataLink(prev => ([...prev, {"link": link}]));
        setLink('')
    }



    useEffect(() => {
        fetch("http://localhost:5001/api")
        .then(response => response.json())
        .then(list => setList(list))
        .catch((e) => {console.log("Error - не могу считать", e)})
    }, [])






    return(
      <>
        <form onSubmit={Send_data_to_serv}>
            <input type='text' placeholder='Title' onChange={(e) => setTitle(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Text' onChange={(e) => setText(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Link' onChange={(e) => setLink(e.target.value)} value={link}/>
            <button onClick={plusLink}>plus</button><br/><br/>
            <button type='submit'>Send</button>
        </form><br/><br/>

        <div>
            {
                data_link.map(elem => (
                    <div>{elem.link}</div>
                ))
            }
        </div>

        <div className='list_main'>
            {
                list.map(item => (
                    <div className='list_item'>
                        <h3>{item[1]}</h3>
                        <p>{item[2]}</p>
                        {
                            item[4] != null || JSON.parse(item[4]) != null ? (
                            JSON.parse(item[4]).map(elem => (
                                <div>{elem.link}</div>
                            ))
                            ) : (
                            <div></div>
                            )
                        }
                       <button onClick={() => {deleteItem(item[0])}}>Delent</button>
                    </div>
                ))
            }
        </div>
      </>
    );
}

export default Admin;
