import {useState, useEffect} from 'react'
import './style/AdminPage.css'
import Header from './Header';

function Admin(){
    const [link, setLink] = useState('');
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [list, setList] = useState([]);

    const [data_link, setDataLink] = useState([]);


    const Send_data_to_serv = async(e) => {
        e.preventDefault()
        try{
            const form_data = new FormData();
            form_data.append('title', title);
            form_data.append('text', text);
            form_data.append('json_link', JSON.stringify(data_link))

            const response = await fetch(
                "http://localhost:5001/send_from_api",{
                    method: "POST",
                    body: form_data
                }
            )

            const data = await response.json()
            console.log(data.res)

            setTitle('');
            setText('');
            setLink('');
            setDataLink([]);
        }
        catch(e){
            console.log("Error: ", e);
        }
    }

    const deleteItem = async (id) => {
        try{
            const form_data = new FormData()
            form_data.append('del', id)
            const response = await fetch(
                "http://localhost:5001/delete_item", {
                    method: "POST",
                    body: form_data
                }
            )
            const data = await response.json()
            console.log(data.res)

            setList(list.filter(item => item[0] !== id))
        }
        catch(err){
            console.log(err)
        }
    }

    const plusLink = (e) => {
        e.preventDefault();
        setDataLink(prev => [...prev, {"link":link}])
        setLink('')

    }

    const delete_link = (e, index) => {
        e.preventDefault()
        setDataLink(data_link.filter(link => link[index] != index))

    }


    useEffect(() => {
        fetch("http://localhost:5001/api")
        .then(response => response.json())
        .then(list => setList(list))
        .catch((e) => {console.log("Error - не могу считать", e)})
    }, [])


    return(
      <>
        <Header/>

        <form onSubmit={Send_data_to_serv}>
            <input type='text' placeholder='Title' onChange={(e) => setTitle(e.target.value)} value={title}/><br/><br/>
            <input type='text' placeholder='Text' onChange={(e) => setText(e.target.value)} value={text}/><br/><br/>
            <input type='text' placeholder='Link' onChange={(e) => setLink(e.target.value)} value={link}/><br/><br/>            
            <button onClick={plusLink}>plus</button><br/><br/>
            <button type='submit'>Send</button>
        </form><br/><br/>

        {
            data_link.length != 0 ? (
                data_link.map((elem, index) => (
                    <>
                        <li>{elem.link}<button onClick={() => delete_link(index)}>Del link</button></li>
                    </>
                ))
            ) : (
                <div></div>
            )
        }



        <div className='list_main'>
            {
                list.map(item => (
                    <div className='list_item'>
                        <h3>{item[1]}</h3>
                        <p>{item[2]}</p>
                        {
                            JSON.parse(item[3]) != null && JSON.parse(item[3]).length != 0 ? (
                                JSON.parse(item[3]).map(elem => (
                                    <img src={elem.link} className='photo'/>
                                ))
                            ) : (
                                <div>No Photo</div>
                            )
                        } 
                        <br/><br/>                 
                       <button onClick={() => {deleteItem(item[0])}}>Delent</button>
                    </div>
                ))
            }
        </div>
      </>
    );
}

export default Admin;
