import {useState, useEffect} from 'react'

function Admin(){
    const [link, setLink] = useState('');
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');

    const Send_data_to_serv = async(e) => {
        e.preventDefault();
        try{
            const form_data = new FormData();
            form_data.append('title', title);
            form_data.append('text', text);
            form_data.append('link', link);

            const response = await fetch(
                "http://localhost:5001/send_from_api",{
                    method: "POST",
                    body: form_data
                }
            )

            const data = await response.json()
            console.log(data.res)
        }
        catch(e){
            console.log("Error: ", e);
        }
    }

    return(
      <>
        <form onSubmit={Send_data_to_serv}>
            <input type='text' placeholder='Title' onChange={(e) => setTitle(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Text' onChange={(e) => setText(e.target.value)}/><br/><br/>
            <input type='text' placeholder='Link' onChange={(e) => setLink(e.target.value)}/><br/><br/>
            <button type='submit'>Send</button>
        </form>
      </>
    );
}

export default Admin;
